import React from 'react'

const BoardInfo = () => {
    const boardInfoVal = [ { "id": 1, "self": "https://api.atlassian.com/ex/jira/0738b40b-1a37-4575-ac41-0ad7f49b529d/rest/agile/1.0/board/1", "name": "CG3 board", "type": "simple", "location": { "projectId": 10000, "displayName": "Capstone Group 3 (CG3)", "projectName": "Capstone Group 3", "projectKey": "CG3", "projectTypeKey": "software", "avatarURI": "https://api.atlassian.com/ex/jira/0738b40b-1a37-4575-ac41-0ad7f49b529d/rest/api/2/universal_avatar/view/type/project/avatar/10415?size=small", "name": "Capstone Group 3 (CG3)" } } ] 

    const [boardMsg, setBoardMsg] = useState([{}])
    const url = 'https://jira-velocity-data-processing.herokuapp.com/board/';
    useEffect(async () => {
        const response = await fetch(url, {
        method: 'POST',
        headers: {"Content-Type": "application/json"},
        body: boardInfoVal});

        setBoardMsg(JSON.stringify(response));
        }, [boardMsg]);

    return(
        <div>
            <h1>
                {boardMsg}
            </h1>
        </div>
    )
}
